css:
.file-monitoring:after {
  content: "M";
  width: 20px;
  height: 20px;
  color: #45A815;
  position: absolute;
  left: 10px;
  text-align: center;
  border-radius: 50%;
  line-height: 20px;
}
.file-monitoring.pulse:after {
  -webkit-animation-name: greenPulse;
  -webkit-animation-duration: 2s;
  -webkit-animation-iteration-count: infinite;
}
@-webkit-keyframes greenPulse {
  from {
    -webkit-box-shadow: 0 0 9px transparentize(#45A815, 0.8);
  }
  50% {
    -webkit-box-shadow: 0 0 18px #327b0f;
  }
  to {
    -webkit-box-shadow: 0 0 9px transparentize(#45A815, 0.8);
  }
}
